import React, { useEffect } from "react";
import { useParams } from "react-router-native";
import { GetPhoneInfo } from "../../redux/actions/actions";
import { useDispatch, useSelector } from "react-redux";
import { Button, Card, Title, Paragraph } from 'react-native-paper';

export default function Phone() {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    dispatch(GetPhoneInfo(id));
  }, [dispatch]);
  const phone = useSelector((state) => state.phoneInfo);


  return (
    <Card>
      <Card.Content>
        <Title>{phone.brand}</Title>
        <Paragraph>{phone.phone_name}</Paragraph>
      </Card.Content>
      <Card.Cover style={{ width: 410, height: 540, marginLeft:12}} source={{ uri: phone?.phone_images ? phone?.phone_images[0] : null }} />
      <Card.Content>
        <Paragraph>{phone.brand}</Paragraph>
        <Paragraph>{phone.os}</Paragraph>
      </Card.Content>
    </Card>
  );
}
